import React, { useState, useEffect } from 'react';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');
  const [debugInfo, setDebugInfo] = useState('');
  const [testToken, setTestToken] = useState('');

  useEffect(() => {
    // Check for existing token and fetch todos
    const token = getStoredToken();
    if (token) {
      setTestToken(token);
      fetchTodos();
    } else {
      addDebugInfo('No token found in localStorage');
    }
  }, []);

  const addDebugInfo = (info) => {
    console.log(info);
    const timestamp = new Date().toLocaleTimeString();
    setDebugInfo(prev => prev + `\n${timestamp}: ${info}`);
  };

  const getStoredToken = () => {
    // In a real app, this would get from localStorage
    // For testing, we'll use the state variable
    return testToken || localStorage.getItem('token') || '';
  };

  const getAuthHeaders = () => {
    const token = getStoredToken();
    addDebugInfo(`Getting auth headers with token: ${token ? token.substring(0, 10) + '...' : 'NO TOKEN'}`);
    
    if (!token) {
      addDebugInfo('No token available - this will likely fail');
      return {
        'Content-Type': 'application/json'
      };
    }
    
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}` // Make sure this matches your curl format
    };
  };

  const testConnection = async () => {
    addDebugInfo('Testing connection to backend...');
    
    try {
      const response = await fetch('http://localhost:5000/api/todos', {
        method: 'GET',
        headers: getAuthHeaders()
      });
      
      addDebugInfo(`Response status: ${response.status}`);
      addDebugInfo(`Response headers: ${JSON.stringify([...response.headers.entries()])}`);
      
      const text = await response.text();
      addDebugInfo(`Raw response: ${text.substring(0, 200)}${text.length > 200 ? '...' : ''}`);
      
      if (response.ok) {
        try {
          const data = JSON.parse(text);
          addDebugInfo(`Parsed JSON successfully: ${data.length} items`);
          setTodos(data);
          setError('');
        } catch (e) {
          addDebugInfo(`Failed to parse JSON: ${e.message}`);
          setError('Server returned invalid JSON');
        }
      } else {
        addDebugInfo(`Request failed with status ${response.status}`);
        setError(`Server error: ${response.status} - ${text}`);
      }
      
    } catch (error) {
      addDebugInfo(`Network error: ${error.name} - ${error.message}`);
      
      if (error.name === 'TypeError' && error.message.includes('fetch')) {
        setError('Cannot connect to server. Is your backend running on http://localhost:5000?');
      } else if (error.message.includes('CORS')) {
        setError('CORS error. Backend needs to allow requests from your frontend.');
      } else {
        setError(`Network error: ${error.message}`);
      }
    }
  };

  const fetchTodos = async () => {
    addDebugInfo('=== Starting fetchTodos ===');
    setIsLoading(true);
    setError('');
    
    try {
      const headers = getAuthHeaders();
      addDebugInfo(`Request URL: http://localhost:5000/api/todos`);
      addDebugInfo(`Request headers: ${JSON.stringify(headers, null, 2)}`);
      
      const response = await fetch('http://localhost:5000/api/todos', {
        method: 'GET',
        headers: headers
      });

      addDebugInfo(`Response status: ${response.status}`);
      addDebugInfo(`Response ok: ${response.ok}`);
      
      // Log response headers
      const responseHeaders = {};
      response.headers.forEach((value, key) => {
        responseHeaders[key] = value;
      });
      addDebugInfo(`Response headers: ${JSON.stringify(responseHeaders, null, 2)}`);
      
      const data = await response.json();
      addDebugInfo(`Response data type: ${typeof data}`);
      addDebugInfo(`Response data: ${JSON.stringify(data).substring(0, 500)}`);
      
      if (!response.ok) {
        if (response.status === 401) {
          addDebugInfo('401 Unauthorized - token might be invalid');
          localStorage.removeItem('token');
          setError('Authentication failed. Please check your token.');
          return;
        }
        throw new Error(data.message || `HTTP ${response.status}`);
      }

      addDebugInfo(`Successfully fetched ${Array.isArray(data) ? data.length : 'non-array'} todos`);
      setTodos(Array.isArray(data) ? data : []);
      setError('');
      
    } catch (error) {
      addDebugInfo(`=== FETCH ERROR ===`);
      addDebugInfo(`Error name: ${error.name}`);
      addDebugInfo(`Error message: ${error.message}`);
      addDebugInfo(`Error stack: ${error.stack?.substring(0, 300)}`);
      
      if (error.name === 'TypeError' && error.message.includes('Failed to fetch')) {
        setError('Cannot connect to server. Check if backend is running on http://localhost:5000');
      } else if (error.name === 'SyntaxError') {
        setError('Server returned invalid JSON. Check backend response format.');
      } else {
        setError(error.message || 'Failed to load todos');
      }
    } finally {
      setIsLoading(false);
      addDebugInfo('=== fetchTodos completed ===');
    }
  };

  const addTodo = async () => {
    if (!newTodo.trim()) {
      setError('Todo text cannot be empty');
      return;
    }

    addDebugInfo('=== Starting addTodo ===');
    addDebugInfo(`Adding todo: "${newTodo.trim()}"`);

    try {
      setIsLoading(true);
      setError('');
      
      const headers = getAuthHeaders();
      const payload = {
        title: newTodo.trim(),
        description: '',
        completed: false
      };
      
      addDebugInfo(`POST payload: ${JSON.stringify(payload)}`);
      addDebugInfo(`POST headers: ${JSON.stringify(headers, null, 2)}`);
      
      const response = await fetch('http://localhost:5000/api/todos', {
        method: 'POST',
        headers: headers,
        body: JSON.stringify(payload)
      });

      addDebugInfo(`POST response status: ${response.status}`);
      
      const data = await response.json();
      addDebugInfo(`POST response data: ${JSON.stringify(data)}`);
      
      if (!response.ok) {
        throw new Error(data.message || `HTTP ${response.status}`);
      }

      setTodos([...todos, data]);
      setNewTodo('');
      setError('');
      addDebugInfo(`Successfully added todo: "${data.title}"`);
      
    } catch (error) {
      addDebugInfo(`ADD TODO ERROR: ${error.message}`);
      setError(error.message || 'Failed to add todo');
    } finally {
      setIsLoading(false);
      addDebugInfo('=== addTodo completed ===');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addTodo();
    }
  };

  const toggleTodo = async (id) => {
    const todo = todos.find(t => t._id === id);
    if (!todo) return;

    try {
      setIsLoading(true);
      const response = await fetch(`http://localhost:5000/api/todos/${id}`, {
        method: 'PUT',
        headers: getAuthHeaders(),
        body: JSON.stringify({ completed: !todo.completed })
      });

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Failed to update todo');
      }

      setTodos(todos.map(t => t._id === id ? data : t));
    } catch (error) {
      addDebugInfo(`Toggle error: ${error.message}`);
      setError(error.message || 'Failed to update todo status.');
    } finally {
      setIsLoading(false);
    }
  };

  const deleteTodo = async (id) => {
    const todo = todos.find(t => t._id === id);
    if (!todo || !window.confirm('Are you sure you want to delete this todo?')) return;

    try {
      setIsLoading(true);
      const response = await fetch(`http://localhost:5000/api/todos/${id}`, {
        method: 'DELETE',
        headers: getAuthHeaders()
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.message || 'Failed to delete todo');
      }

      setTodos(todos.filter(t => t._id !== id));
    } catch (error) {
      addDebugInfo(`Delete error: ${error.message}`);
      setError(error.message || 'Failed to delete todo');
    } finally {
      setIsLoading(false);
    }
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });

  const activeCount = todos.filter(todo => !todo.completed).length;
  const completedCount = todos.filter(todo => todo.completed).length;

  return (
    <div className="max-w-6xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800">My Todos - Debug Mode</h2>
        <div className="flex gap-4 text-sm text-gray-600">
          <span className="bg-blue-100 px-2 py-1 rounded">Active: {activeCount}</span>
          <span className="bg-green-100 px-2 py-1 rounded">Completed: {completedCount}</span>
        </div>
      </div>

      {/* Token Input for Testing */}
      <div className="mb-6 p-4 bg-gray-50 rounded-md">
        <h3 className="font-semibold mb-2">Authentication Token:</h3>
        <div className="flex gap-2">
          <input
            type="text"
            value={testToken}
            onChange={(e) => setTestToken(e.target.value)}
            placeholder="Paste your authentication token here"
            className="flex-1 px-3 py-2 border border-gray-300 rounded text-sm"
          />
          <button
            onClick={() => {
              if (testToken) {
                localStorage.setItem('token', testToken);
                addDebugInfo('Token saved to localStorage');
              }
            }}
            className="px-4 py-2 bg-gray-600 text-white rounded text-sm hover:bg-gray-700"
          >
            Save Token
          </button>
        </div>
      </div>

      {/* Test Buttons */}
      <div className="mb-6 flex gap-2">
        <button
          onClick={testConnection}
          className="px-4 py-2 bg-purple-500 text-white rounded hover:bg-purple-600"
          disabled={isLoading}
        >
          Test Connection
        </button>
        <button
          onClick={fetchTodos}
          className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
          disabled={isLoading}
        >
          Fetch Todos
        </button>
        <button
          onClick={() => setDebugInfo('')}
          className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600"
        >
          Clear Debug
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="flex gap-2 mb-6">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Add a new todo..."
          className="flex-1 px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          disabled={isLoading}
        />
        <button 
          onClick={addTodo}
          className="px-6 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed"
          disabled={isLoading}
        >
          {isLoading ? 'Adding...' : 'Add'}
        </button>
      </div>

      <div className="flex gap-2 mb-6">
        {['all', 'active', 'completed'].map(filterType => (
          <button
            key={filterType}
            onClick={() => setFilter(filterType)}
            className={`px-4 py-2 rounded-md ${
              filter === filterType 
                ? 'bg-blue-500 text-white' 
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            } disabled:opacity-50`}
            disabled={isLoading}
          >
            {filterType.charAt(0).toUpperCase() + filterType.slice(1)}
          </button>
        ))}
      </div>

      {isLoading && (
        <div className="text-center py-4 text-gray-500">
          <div className="inline-block animate-spin rounded-full h-6 w-6 border-b-2 border-gray-900"></div>
          <span className="ml-2">Loading...</span>
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Todos List */}
        <div>
          <h3 className="text-lg font-semibold mb-4">Todos ({filteredTodos.length})</h3>
          <div className="space-y-2">
            {filteredTodos.length === 0 ? (
              <div className="text-center py-8 text-gray-500">
                {filter === 'all' ? 'No todos yet. Add one above!' : 
                 filter === 'active' ? 'No active todos.' : 
                 'No completed todos.'}
              </div>
            ) : (
              filteredTodos.map(todo => (
                <div 
                  key={todo._id} 
                  className={`flex items-center gap-3 p-3 border rounded-md ${
                    todo.completed ? 'bg-gray-50 opacity-75' : 'bg-white'
                  }`}
                >
                  <input
                    type="checkbox"
                    checked={todo.completed}
                    onChange={() => toggleTodo(todo._id)}
                    className="w-4 h-4 text-blue-600"
                    disabled={isLoading}
                  />
                  
                  <span 
                    className={`flex-1 cursor-pointer ${
                      todo.completed ? 'line-through text-gray-500' : 'text-gray-800'
                    }`}
                    onClick={() => toggleTodo(todo._id)}
                  >
                    {todo.title}
                  </span>

                  <div className="flex gap-2">
                    <button
                      onClick={() => deleteTodo(todo._id)}
                      className="px-2 py-1 text-red-600 hover:bg-red-50 rounded text-sm disabled:opacity-50"
                      disabled={isLoading}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Debug Information */}
        <div>
          <h3 className="text-lg font-semibold mb-4">Debug Information</h3>
          <div className="bg-black text-green-400 p-4 rounded-md font-mono text-xs max-h-96 overflow-y-auto">
            <pre className="whitespace-pre-wrap">
              {debugInfo || 'No debug information yet...\nTry clicking "Test Connection" or "Fetch Todos"'}
            </pre>
          </div>
        </div>
      </div>

      {/* Troubleshooting Tips */}
      <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-md">
        <h3 className="font-semibold text-yellow-800 mb-2">Since your curl works, check these:</h3>
        <ul className="text-sm text-yellow-700 space-y-1">
          <li>üîë <strong>Token format:</strong> Make sure the token in localStorage matches what you use in curl</li>
          <li>üì± <strong>Headers:</strong> Compare the exact headers being sent by frontend vs curl</li>
          <li>üåê <strong>CORS:</strong> Frontend requests from different origin than curl - backend needs CORS setup</li>
          <li>üîç <strong>Network tab:</strong> Check browser DevTools Network tab to see actual request details</li>
          <li>‚ö° <strong>Content-Type:</strong> Ensure backend expects 'application/json' content type</li>
        </ul>
      </div>
    </div>
  );
}

export default TodoList;
