import React, { useState, useEffect } from 'react';
import './TodoList.css';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');
  const [isAuthenticating, setIsAuthenticating] = useState(false);

  // Remove the autoLogin function since we're using the Login component now

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      console.log('No token found in localStorage');
      return null;
    }
    console.log('Token found:', token.substring(0, 20) + '...');
    console.log('Full Authorization header:', `Bearer ${token}`);
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const makeAuthenticatedRequest = async (url, options = {}) => {
    const headers = getAuthHeaders();
    if (!headers) {
      console.log('No headers, attempting auto-login...');
      // Try to auto-login if no token
      await autoLogin();
      const newHeaders = getAuthHeaders();
      if (!newHeaders) return null;
      return makeAuthenticatedRequest(url, options); // Retry with new token
    }

    try {
      console.log('Making request to:', url);
      console.log('Request headers:', headers);
      console.log('Request options:', options);
      
      const response = await fetch(url, {
        ...options,
        headers: { ...headers, ...options.headers }
      });

      console.log('Response status:', response.status);
      console.log('Response headers:', Object.fromEntries(response.headers.entries()));
      
      if (response.status === 401) {
        console.log('401 error, token expired. Re-authenticating...');
        // Token expired, try to re-login
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        await autoLogin();
        const newHeaders = getAuthHeaders();
        if (newHeaders) {
          // Retry the request with new token
          console.log('Retrying request with new token...');
          return await fetch(url, {
            ...options,
            headers: { ...newHeaders, ...options.headers }
          });
        }
        return null;
      }
      return response;
    } catch (err) {
      console.error('Network error:', err);
      setError('Network error. Please check your connection.');
      return null;
    }
  };

  const fetchTodos = async () => {
    setIsLoading(true);
    setError('');
    console.log('Fetching todos...');
    const response = await makeAuthenticatedRequest('http://localhost:5000/api/todos');
    if (!response) {
      setIsLoading(false);
      return;
    }
    try {
      const data = await response.json();
      console.log('Todos response:', data);
      if (!response.ok) throw new Error(data.error || data.message || 'Failed to fetch todos');
      setTodos(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Fetch todos error:', err);
      setError(err.message || 'Failed to load todos.');
    }
    setIsLoading(false);
  };

  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.trim()) {
      setError('Todo text cannot be empty');
      return;
    }
    setIsLoading(true);
    setError('');
    console.log('Adding todo:', newTodo);
    const response = await makeAuthenticatedRequest('http://localhost:5000/api/todos', {
      method: 'POST',
      body: JSON.stringify({ title: newTodo.trim(), description: '', completed: false })
    });
    if (!response) {
      setIsLoading(false);
      return;
    }
    try {
      const data = await response.json();
      console.log('Add todo response:', data);
      if (!response.ok) throw new Error(data.error || data.message || 'Failed to add todo');
      setTodos([...todos, data]);
      setNewTodo('');
    } catch (err) {
      console.error('Add todo error:', err);
      setError(err.message || 'Failed to add todo.');
    }
    setIsLoading(false);
  };

  const startEdit = (todo) => {
    setEditId(todo.id);
    setEditText(todo.title);
  };

  const cancelEdit = () => {
    setEditId(null);
    setEditText('');
  };

  const saveEdit = async (id) => {
    if (!editText.trim()) {
      setError('Todo text cannot be empty');
      return;
    }
    setIsLoading(true);
    setError('');
    const response = await makeAuthenticatedRequest(`http://localhost:5000/api/todos/${id}`, {
      method: 'PUT',
      body: JSON.stringify({ title: editText.trim() })
    });
    if (!response) {
      setIsLoading(false);
      return;
    }
    try {
      const data = await response.json();
      if (!response.ok) throw new Error(data.error || data.message || 'Failed to update todo');
      setTodos(todos.map(t => t.id === id ? data : t));
      setEditId(null);
      setEditText('');
    } catch (err) {
      setError(err.message || 'Failed to update todo.');
    }
    setIsLoading(false);
  };

  const toggleTodo = async (id) => {
    const todo = todos.find(t => t.id === id);
    if (!todo) return;
    setError('');
    const response = await makeAuthenticatedRequest(`http://localhost:5000/api/todos/${id}`, {
      method: 'PUT',
      body: JSON.stringify({ completed: !todo.completed })
    });
    if (!response) return;
    try {
      const data = await response.json();
      if (!response.ok) throw new Error(data.error || data.message || 'Failed to update todo');
      setTodos(todos.map(t => t.id === id ? data : t));
    } catch (err) {
      setError(err.message || 'Failed to update todo status.');
    }
  };

  const deleteTodo = async (id) => {
    if (!window.confirm('Are you sure you want to delete this todo?')) return;
    setError('');
    const response = await makeAuthenticatedRequest(`http://localhost:5000/api/todos/${id}`, {
      method: 'DELETE'
    });
    if (!response) return;
    if (!response.ok) {
      try {
        const data = await response.json();
        throw new Error(data.error || data.message || 'Failed to delete todo');
      } catch (err) {
        setError(err.message || 'Failed to delete todo.');
        return;
      }
    }
    setTodos(todos.filter(t => t.id !== id));
  };

  // Manual refresh - redirect to login instead of auto-login
  const refreshAuth = () => {
    console.log('Clearing auth and redirecting to login...');
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setError('Please log in again');
    // Uncomment to redirect to login page
    // window.location.href = '/login';
  };

  // Logout function
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeUser('user');
    setError('Logged out successfully');
    // Uncomment to redirect to login page
    // window.location.href = '/login';
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });

  const activeCount = todos.filter(todo => !todo.completed).length;
  const completedCount = todos.filter(todo => todo.completed).length;

  if (isAuthenticating) {
    return (
      <div className="todo-list-container">
        <div className="loading">Authenticating...</div>
      </div>
    );
  }

  return (
    <div className="todo-list-container">
      <div className="todo-header">
        <h2>My Todos</h2>
        <div className="todo-stats">
          <span>Active: {activeCount}</span>
          <span>Completed: {completedCount}</span>
          <button onClick={refreshAuth} className="refresh-auth-btn" title="Clear Auth">
            ðŸ”„
          </button>
          <button onClick={logout} className="logout-btn" title="Logout">
            ðŸšª Logout
          </button>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError('')} className="error-close" aria-label="Close error">Ã—</button>
        </div>
      )}

      {/* Debug info */}
      <div style={{background: '#f0f0f0', padding: '10px', margin: '10px 0', fontSize: '12px'}}>
        <strong>Debug Info:</strong><br/>
        Token in localStorage: {localStorage.getItem('token') ? 'Yes (' + localStorage.getItem('token').substring(0, 20) + '...)' : 'No'}<br/>
        User in localStorage: {localStorage.getItem('user') ? 'Yes' : 'No'}<br/>
        Current Auth Header: {localStorage.getItem('token') ? `Bearer ${localStorage.getItem('token').substring(0, 20)}...` : 'None'}
      </div>

      <form onSubmit={addTodo} className="add-todo-form">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="Add a new todo..."
          className="todo-input"
          disabled={isLoading}
        />
        <button type="submit" className="add-btn" disabled={isLoading || !newTodo.trim()}>
          {isLoading ? 'Adding...' : 'Add'}
        </button>
      </form>

      <div className="filter-buttons">
        <button onClick={() => setFilter('all')} className={filter === 'all' ? 'active' : ''} disabled={isLoading}>
          All ({todos.length})
        </button>
        <button onClick={() => setFilter('active')} className={filter === 'active' ? 'active' : ''} disabled={isLoading}>
          Active ({activeCount})
        </button>
        <button onClick={() => setFilter('completed')} className={filter === 'completed' ? 'active' : ''} disabled={isLoading}>
          Completed ({completedCount})
        </button>
      </div>

      {isLoading && todos.length === 0 ? (
        <div className="loading">Loading todos...</div>
      ) : (
        <div className="todos-list">
          {filteredTodos.length === 0 ? (
            <div className="no-todos">
              {filter === 'all' ? 'No todos yet. Add one above!' : 
               filter === 'active' ? 'No active todos.' : 
               'No completed todos.'}
            </div>
          ) : (
            filteredTodos.map(todo => (
              <div key={todo.id} className={`todo-item ${todo.completed ? 'completed' : ''} ${isLoading ? 'loading' : ''}`}>
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => toggleTodo(todo.id)}
                  className="todo-checkbox"
                  disabled={isLoading}
                />
                {editId === todo.id ? (
                  <input
                    type="text"
                    value={editText}
                    onChange={(e) => setEditText(e.target.value)}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') saveEdit(todo.id);
                      if (e.key === 'Escape') cancelEdit();
                    }}
                    className="edit-input"
                    autoFocus
                  />
                ) : (
                  <span className="todo-text" onDoubleClick={() => startEdit(todo)}>
                    {todo.title}
                  </span>
                )}
                <div className="todo-actions">
                  {editId === todo.id ? (
                    <>
                      <button onClick={() => saveEdit(todo.id)} className="save-btn" disabled={isLoading || !editText.trim()}>Save</button>
                      <button onClick={cancelEdit} className="cancel-btn" disabled={isLoading}>Cancel</button>
                    </>
                  ) : (
                    <>
                      <button onClick={() => startEdit(todo)} className="edit-btn" disabled={isLoading}>Edit</button>
                      <button onClick={() => deleteTodo(todo.id)} className="delete-btn" disabled={isLoading}>Ã—</button>
                    </>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default TodoList;
