import React, { useState, useEffect } from 'react';
import './TodoList.css';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');
  const [editId, setEditId] = useState(null);
  const [editText, setEditText] = useState('');

  useEffect(() => {
    fetchTodos();
  }, []);

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      window.location.href = '/login';
      return {};
    }
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const fetchTodos = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:5000/api/todos', {
        headers: getAuthHeaders()
      });

      const data = await response.json();
      
      if (!response.ok) {
        if (response.status === 401) {
          localStorage.removeItem('token');
          window.location.href = '/login';
          return;
        }
        throw new Error(data.message || 'Failed to fetch todos');
      }

      setTodos(data);
      setError('');
    } catch (error) {
      console.error('Fetch error:', error);
      setError(error.message || 'Failed to load todos. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const addTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.trim()) {
      setError('Todo text cannot be empty');
      return;
    }

    try {
      setIsLoading(true);
      const response = await fetch('http://localhost:5000/api/todos', {
        method: 'POST',
        headers: getAuthHeaders(),
        body: JSON.stringify({ 
          title: newTodo.trim(),
          description: '',
          completed: false 
        })
      });

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Failed to add todo');
      }

      setTodos([...todos, data]);
      setNewTodo('');
      setError('');
    } catch (error) {
      console.error('Add todo error:', error);
      setError(error.message || 'Failed to add todo. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const startEdit = (todo) => {
    setEditId(todo._id);
    setEditText(todo.title);
  };

  const saveEdit = async (id) => {
    if (!editText.trim()) {
      setError('Todo text cannot be empty');
      return;
    }

    try {
      setIsLoading(true);
      const response = await fetch(`http://localhost:5000/api/todos/${id}`, {
        method: 'PUT',
        headers: getAuthHeaders(),
        body: JSON.stringify({ 
          title: editText.trim(),
          description: '' 
        })
      });

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Failed to update todo');
      }

      setTodos(todos.map(t => t._id === id ? data : t));
      setEditId(null);
      setError('');
    } catch (error) {
      console.error('Update error:', error);
      setError(error.message || 'Failed to update todo. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const toggleTodo = async (id) => {
    const todo = todos.find(t => t._id === id);
    if (!todo) return;

    try {
      setIsLoading(true);
      const response = await fetch(`http://localhost:5000/api/todos/${id}`, {
        method: 'PUT',
        headers: getAuthHeaders(),
        body: JSON.stringify({ completed: !todo.completed })
      });

      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || 'Failed to update todo');
      }

      setTodos(todos.map(t => t._id === id ? data : t));
    } catch (error) {
      console.error('Toggle error:', error);
      setError(error.message || 'Failed to update todo status.');
    } finally {
      setIsLoading(false);
    }
  };

  const deleteTodo = async (id) => {
    if (!window.confirm('Are you sure you want to delete this todo?')) return;

    try {
      setIsLoading(true);
      const response = await fetch(`http://localhost:5000/api/todos/${id}`, {
        method: 'DELETE',
        headers: getAuthHeaders()
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.message || 'Failed to delete todo');
      }

      setTodos(todos.filter(t => t._id !== id));
    } catch (error) {
      console.error('Delete error:', error);
      setError(error.message || 'Failed to delete todo. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const filteredTodos = todos.filter(todo => {
    if (filter === 'active') return !todo.completed;
    if (filter === 'completed') return todo.completed;
    return true;
  });

  const activeCount = todos.filter(todo => !todo.completed).length;
  const completedCount = todos.filter(todo => todo.completed).length;

  return (
    <div className="todo-list-container">
      <div className="todo-header">
        <h2>My Todos</h2>
        <div className="todo-stats">
          <span>Active: {activeCount}</span>
          <span>Completed: {completedCount}</span>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <form onSubmit={addTodo} className="add-todo-form">
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="Add a new todo..."
          className="todo-input"
          disabled={isLoading}
        />
        <button 
          type="submit" 
          className="add-btn"
          disabled={isLoading}
        >
          {isLoading ? 'Adding...' : 'Add'}
        </button>
      </form>

      <div className="filter-buttons">
        <button
          onClick={() => setFilter('all')}
          className={filter === 'all' ? 'active' : ''}
          disabled={isLoading}
        >
          All
        </button>
        <button
          onClick={() => setFilter('active')}
          className={filter === 'active' ? 'active' : ''}
          disabled={isLoading}
        >
          Active
        </button>
        <button
          onClick={() => setFilter('completed')}
          className={filter === 'completed' ? 'active' : ''}
          disabled={isLoading}
        >
          Completed
        </button>
      </div>

      {isLoading && !todos.length ? (
        <div className="loading">Loading todos...</div>
      ) : (
        <div className="todos-list">
          {filteredTodos.length === 0 ? (
            <div className="no-todos">
              {filter === 'all' ? 'No todos yet. Add one above!' : 
               filter === 'active' ? 'No active todos.' : 
               'No completed todos.'}
            </div>
          ) : (
            filteredTodos.map(todo => (
              <div 
                key={todo._id} 
                className={`todo-item ${todo.completed ? 'completed' : ''}`}
              >
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => toggleTodo(todo._id)}
                  className="todo-checkbox"
                  disabled={isLoading}
                />
                
                {editId === todo._id ? (
                  <input
                    type="text"
                    value={editText}
                    onChange={(e) => setEditText(e.target.value)}
                    className="edit-input"
                    autoFocus
                  />
                ) : (
                  <span 
                    className="todo-text"
                    onClick={() => toggleTodo(todo._id)}
                  >
                    {todo.title}
                  </span>
                )}

                <div className="todo-actions">
                  {editId === todo._id ? (
                    <>
                      <button
                        onClick={() => saveEdit(todo._id)}
                        className="save-btn"
                        disabled={isLoading}
                      >
                        Save
                      </button>
                      <button
                        onClick={() => setEditId(null)}
                        className="cancel-btn"
                        disabled={isLoading}
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button
                        onClick={() => startEdit(todo)}
                        className="edit-btn"
                        disabled={isLoading}
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => deleteTodo(todo._id)}
                        className="delete-btn"
                        disabled={isLoading}
                      >
                        Ã—
                      </button>
                    </>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default TodoList;
